      }
    }
  };

  const handleChildrenClick = () => {
    inputRef?.current?.click();
  };
  return (
    <React.Fragment>
      <input type="file" accept="image/*" ref={inputRef} onChange={handleChange} style={{ display: 'none' }} />
      <div onClick={handleChildrenClick}>{children}</div>
    </React.Fragment>
  );
};
