            </StyledSearch>
            <InfiniteScroll
              next={infiniteNext}
              hasMore={infiniteHasMore}
              dataLength={infiniteSkip}
              height={220}
              loader={<BGLoadingSpinner isLoading={isFetching} />}
            >
