                {optionList.length > 0 && (
                  <div className="row" data-testid="check-all-button" onClick={toggleAllChecked}>
                    <BGCheckbox checked={isAllChecked} size={20} style={{ marginRight: '0.5em' }} />
                    <div className="label">
