                ))}
                {isNoData && searchKeyword && (
                  <BGEmpty title={i18next.t('"{{keyword}}"ë¥¼ í¬í•¨í•˜ëŠ” ë°ì´í„°ê°€ ì—†ìŠµë‹ˆë‹¤.', { keyword: searchKeyword })} />
