      // api ìš”ì²­
      const { data: newPropertyValueList, isFetching, refetch, fetchNextPage, hasNextPage } = useInfiniteFormattingQuery(
        ['propertyValueList', searchKeyword],
        ({ pageParam = 0 }) =>
          EventPropertyApi.findEventPropValue({
            query,
            limit,
            skip: pageParam,
          }),
    
        {
          ...queryOptions,
          getNextPageParam: () => {
