          },
        },
        formattingResult,
      );
    
      // update
      useEffect(() => {
        if (testOptionData) return;
        if (newPropertyValueList) setOptionList(newPropertyValueList);
      }, [newPropertyValueList]);
    
      const loadMore = useCallback(() => fetchNextPage(), [fetchNextPage]);
    
      useEffect(() => {
