  switch (dialogType) {
    default:
      return { desc: '', buttonType: 'type2' };
    case 'delayMessageUpdate':
    case 'delayMessageCopy':
      return {
        desc: i18nextScanKey('해당 캠페인은 플랜 변경으로 인해 수정 및 복사가 불가능합니다.'),
        buttonType: 'type1',
