      state: State,
      action: Action | LoadAction | UpdateKeywordAction | ToggleOneAction | UpdateOptionListAction,
    ): State => {
      let newState;
      switch (action.type) {
        default:
        case 'RESET':
          newState = initialState;
          break;
        case 'LOAD':
          newState = load(state, action.filter);
          break;
        case 'TOGGLE_ALL_CHECKED':
          newState = toggleAllChecked(state);
