    </StyledLayout>
  );
};

// HOC를 통해 캘린더 데이터 Props 삽입
export const VisitStatus = withCalendarDateRange(WrappedVisitStatus);
````

````typescript
// 공통 로직이 추가된 컴포넌트 사용: Dashboard
import React, { FC, useState } from 'react';
import { BGTab, ITab } from './components/BGTab';
